import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [notesArr, addToArr] = useState([]);

  function addNote(item) {
    addToArr([...notesArr, item]);
  }

  return (
    <div>
      <Header />
      <CreateArea ready={addNote}/>
      {notesArr}.map((singleNote, index) => <Note key={index} title="Note title" content="Note content" />)
      <Footer />
    </div>
  );
}

export default App;
