import React, {useState} from "react";

function CreateArea(props) {
  const [note, createNote] = useState({
    title: "",
    content: ""
  });

function eventHandler(event) {
  const {value, name} = event.target;

  createNote(prev => {
    return {
      ...prev,
      [name]: value
    }
  });
}

function submitToApp(event) {
  props.passNote(note);
}

  return (
    <div>
      <form>
        <input onChange={eventHandler} name="title" value={note.title} placeholder="Title" />
        <textarea onChange={eventHandler} name="content" value={note.content} placeholder="Take a note..." rows="3" />
        <button onClick={submitToApp}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
