import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [notesArr, addToArr] = useState([]);

  function addNote(note) {
    addToArr(prev => {
      return [...prev, note];
    });
  }

  function deleteNotes(id) {
    addToArr(prev => {
      return prev.filter((item, index) => {
        return index != id;
      });
    });
  }

  return (
    <div>
      <Header />
      <CreateArea passNote={addNote}/>
      {notesArr.map((item, index) => <Note key={index} id={index} title={item.title} content={item.content} toDelete={deleteNotes} />)}
      <Footer />
    </div>
  );
}

export default App;
