import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [notesArr, addToArr] = useState([]);

  function addNote(item) {
    addToArr([...notesArr, item]);
    item.title = "";
    item.content = "";
  }

  return (
    <div>
      <Header />
      <CreateArea ready={addNote}/>
      {notesArr.map((note, index) => <Note key={index} title={note.title} content={note.content} />)}
      <Footer />
    </div>
  );
}

export default App;
